create table marchandises  (id int primary key, reference varchar(256), designation varchar(256) NOT NULL, nom_departement_produit varchar(256) NOT NULL, categorie varchar(256) NOT NULL, prix_dachat_precedent double, prix_vente_precedent double, prix_vente_en_gros_precedent double, description_produit varchar(256), stock_dalerte double, stock_minimum double, stock_maximum double, nom_entreprise_client varchar(256), is_service boolean NOT NULL);
