create table stocks (id int primary key NOT NULL, reference varchar(256), designation varchar(256) NOT NULL, nom_departement_produit varchar(256) NOT NULL, categorie varchar(256) NOT NULL, description_produit varchar(256), prix_unitaire double NOT NULL, prix_unitaire_en_gros double NOT NULL, montant_tva double NOT NULL, montant_tva_en_gros double NOT NULL, prix_vente double NOT NULL, prix_vente_en_gros double NOT NULL, quantite_totale double NOT NULL, stock_dalerte double, localisation_stock varchar(256), image_produit mediumblob, date_entree date NOT NULL, date_peremption date, localisation varchar(256) NOT NULL, nom_entreprise_fournisseur varchar(256), lots_entrant double NOT NULL, quantite_par_lot double NOT NULL, historique_stock varchar(6500), historique_stock_actif tinyint, prix_dachat double, reference_recu_dachat varchar(256), enregistreur_stock varchar(256), nom_entreprise_client varchar(256), is_service boolean NOT NULL);
